<script setup lang="ts">
import gsap from 'gsap'
import ScrollTrigger from 'gsap/ScrollTrigger'
import { onMounted, ref } from 'vue'

interface Section {
  index: number
  isVisible: boolean
}

const sections = ref<Section[]>(
  Array.from({ length: 6 }, (_, index) => ({
    index: index + 1,
    isVisible: false
  }))
)

onMounted(() => {
  gsap.registerPlugin(ScrollTrigger)

  const sectionElements = document.querySelectorAll('.section')

  sectionElements.forEach((section, position) => {
    const isLast = position === sectionElements.length - 1

    gsap.timeline({
      scrollTrigger: {
        trigger: section,
        start: 'top top',
        end: '+=100%',
        scrub: true
      }
    })
      .to(section, {
        ease: 'none',
        startAt: { filter: 'brightness(100%) contrast(100%)' },
        filter: isLast ? 'none' : 'brightness(60%) contrast(135%)',
        yPercent: isLast ? 0 : -15
      }, 0)
      .to(section.querySelector('img'), {
        ease: 'power1.in',
        yPercent: -40,
        rotation: -20
      }, 0)
  })
})
</script>

